import React from "react";

class Recipes extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            recipeList: []
            }
        this.componentsOfRecipes();
        this.render();
    }

    componentsOfRecipes() {
        fetch('http://localhost:3000/api/recipes/')
            .then(response => response.json())
            .then(data => this.setState({recipeList: data}));
    }

    recipeComponent(name, ingredients, instructions, image) {
        const ingredientItems = ingredients.map((ingredient) => (this.listItem(ingredient)));
        const instructionItems = instructions.map((instruction) => (this.listItem(instruction)));
        return (
            <div>
                <h1> {name} </h1>

                <h2> The ingredients are: </h2>
                <ul> {ingredientItems} </ul>

                <h2> The instructions are:</h2>
                <ol> {instructionItems} </ol>

                <img src={image} alt={`This is an image of ${name}`}></img>
            </div>
        )
    }

    listItem(ingredient) {
        return <li> {ingredient} </li>
    }

    render() {
        // const recipeComponents = 
        //     this.state.recipeList.map((recipe) => (this.componentsOfRecipes(recipe.name, recipe.ingredients,
        //         recipe.instructions, recipe.image)));
        return (
             <div> 
                Hello world
            </div>
        );
    }
}

export default Recipes;
