import React from "react";

class Recipe{
    constructor(props) {
        this.render();
    }

    state = {
        data: null
    }

    componentDidMount() {
        fetch('http://localhost:3000/api/recipes/')
        .then(response => response.json())
        .then(data => console.log(data));
    }

    componentsOfRecipes() {
        this.state.data = fetch('http://localhost:3000/api/recipes/')
        .then(response => response.json());
    }


    render() {
        const data = this.componentDidMount();
        return (
             <div> 
                <h1> {this.state.data[1]} </h1>
                <p> {data} </p>
            </div>
        );
    }
}

export default Recipe;
